import React,{useState,useEffect} from 'react';
import './offer.css'


import  axios  from "axios";
import Container from '../../../../Container.jsx';

function Offer() {
  const [ProductData,setproductData] = useState([])
  const section1Products = ProductData.slice(0, 8);
  useEffect(() => {
     axios.get("https://66d6dd72006bfbe2e64ee358.mockapi.io/user/Products")
     .then(res => setproductData(res.data))
     .catch(err => console.log(err.message))
          console.log(ProductData);

  },[]);
  return (
<div className="offer">
        <Container>
            <div className="offer-content">
                <h2>Special Offers</h2>
                <p>Get up to 25% off on your favorite products</p>
            </div>
            <div className="offer-products">
                {section1Products.map((product, index) => (
                    <div key={index} className="offer-product">
                        <img src={product.image} alt={product.name} />
                        <h3>{product.name}</h3>
                        <p>From ${product.price}</p>
                        <button>Add to Cart</button>
                    </div>
                ))}
            </div>
        </Container>

 </div>

  );
}

export default Offer;
