import React,{useState,useEffect} from 'react';
import './productmain.css';
import  axios  from "axios";
import Productmodal from './modal/modal.jsx';
import Admintopheader from '../../admintopheader.jsx';





const Modal = ({ isOpen, onClose }) => {
  if (!isOpen) return null;

  return (
    <div style={overlayStyle}>
      <div style={modalStyle}>



        <button className='closebtn' onClick={onClose}><i class="fa-solid fa-xmark"></i></button>
       <Productmodal/>
      </div>
    </div>
  );
};


const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,


};

const modalStyle = {
  backgroundColor: 'white',
  borderRadius: '4px',
  padding: '20px',
  width:  '90%',
  position: 'absolute',
  left: '20rem',
  height: '100vh',
};

function Productmain() {


  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => setModalIsOpen(true);
  const closeModal = () => setModalIsOpen(false);

 const [ProductData,setproductData] = useState([])
  const [url,setUrl] = useState("https://66c4bb55b026f3cc6cf07f5c.mockapi.io/user/Products")

useEffect(() => {
   axios.get(url)
   .then(res => setproductData(res.data))
   .catch(err => console.log(err.message))
        console.log(ProductData);


},[]);
const handleDelete = (id) => {
  // API dan ma'lumotni o'chirish
  axios.delete(`https://66c4bb55b026f3cc6cf07f5c.mockapi.io/user/Products/${id}`)
    .then(() => {
      // O'chirilgan ma'lumotni holatdan o'chirish
      setproductData(ProductData.filter(item => item.id !== id));
    })
    .catch(error => {
      console.error("There was an error deleting the data!", error);
    });
};




  return (
      <main className="productmain">

        <Admintopheader/>

    <div className="productbox">

    <h1 style={{  marginTop: "2rem", fontSize: "3rem" }}>Products</h1>
      <button className='openmodal' onClick={openModal}>Add Product</button>
      <Modal isOpen={modalIsOpen} onClose={closeModal} />

      <div class="table-container">
        <table>
            <thead>
                <tr>
                  <th>#</th>
                  <th>Name</th>
                  <th>Img</th>
                  <th>Title</th>
                  <th>Price</th>
                  <th>OldPrice</th>
                  <th>CategoryName</th>
                  <th>UPDATE</th>
                  <th>DELETE</th>

                </tr>
            </thead>
            <tbody>

                {
                  ProductData.map(item => {
                        return (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.product.name}</td>
                                  <td>{}</td>
                                <td>{item.product.title}</td>
                                <td>${item.product.price}</td>
                                <td>${item.product.oldprice}</td>
                                <td>{item.product.categoryname}</td>
                                 <td><button>UPDATE</button></td>

                                 <td>
                                   <button className='' onClick={() => handleDelete(item.id)}>DELETE</button>
                                    </td>

                            </tr>
                        )

                   })
                }


            </tbody>
        </table>
    </div>


    </div>


      </main>
  );
};

export default Productmain;
