import React,{useState} from 'react';
import './productmain.css';
import  axios  from 'axios';



const Modal = ({ isOpen, onClose }) => {
  if (!isOpen) return null;

  return (
    <div style={overlayStyle}>
      <div style={modalStyle}>

        <button onClick={onClose}>Yopish</button>
        <form onSubmit={hendleSubmit}>
                <input type="text" placeholder="Name" name="name" required  onChange={hendleInput}/>
                <input type="number" placeholder="Price" name="price" required onChange={hendleInput} />
                <input type="number" placeholder="Oldprice" name="oldprice" required onChange={hendleInput}/>
                <input type="text" placeholder="Title" name="title" required  onChange={hendleInput}/>
                <input type="text" placeholder="CategoryName" name="categoryname" required  onChange={hendleInput}/>

                 <button>Submit</button>
             </form>
      </div>
    </div>
  );
};


const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundColor: 'rgba(0, 0, 0, 0.75)',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
};

const modalStyle = {
  backgroundColor: 'white',
  borderRadius: '4px',
  padding: '20px',
  width:  '90%',
  position: 'absolute',
  left: '20rem',
  height: '100vh',
};

function Productmain() {

  const [product, setProduct] = useState({
    name:'',
    price:'',
    oldprice:'',
    title:'',
    categoryname:'',
  })
   const hendleInpu = (e) => {
       setProduct({...product,[e.target.name]: e.target.e})
   }
   function hendleSubmit(event){
     event.preventDefault();
      axios.post("https://66c4bb55b026f3cc6cf07f5c.mockapi.io/user/Products",{product})
      .then(res => console.log(res))
      .catch(err => console.log(err.message))


   }
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => setModalIsOpen(true);
  const closeModal = () => setModalIsOpen(false);
  return (
      <main className="productmain">
     <div className="topicons">
         <input type="search" placeholder='Search' />
          <div className="">

          <h3 className="text-2xl">Hello Abrorbek</h3>
          <img src='' alt="" srcset="" />
          </div>

     </div>


    <div className="productbox">

    <h1>React Modal Misol</h1>
      <button onClick={openModal}>Modalni ochish</button>
      <Modal isOpen={modalIsOpen} onClose={closeModal} />

    </div>


      </main>
  );
};

export default Productmain;
